import ReactGoogleSheets from 'react-google-sheets';
import * as Constants from './Constants'
import React, { Component } from 'react';
import './App.css';

class testDataComponent extends Component {

  constructor(props) {
    super(props)
    this.state = {
      sheetLoaded: false,
      players: {},
      teams: {},
      currentPlayerId: '',
      nextPlayerId: '',
      prevPlayerId: ''

    }
  }
  render() {

    const populatePlayerInfo = (players) => {
      let returnData = {};
      for (let i = 1; i < players.data.length; i++) {
        let playerProfile = {};
        playerProfile.playerId = 1;
        playerProfile.playerId = 2;
        returnData[playerProfile.playerId] = playerProfile;
      }
      return returnData;
    }

    const populateTeams = (mtplTeams) => {
      let returnData = {};
      let teams = mtplTeams.data;
      for (let i = 3; i < teams.length; i++) {
        let teamInfo = {};
        teamInfo.name = teams[i][0];
        teamInfo.ownerName = teams[i][1];
        teamInfo.isOwnerPlayer = teams[i][2];
        teamInfo.amount = teams[i][3];
        teamInfo.amountSpent = teams[i][4];
        teamInfo.amountBalance = teams[i][5];
        teamInfo.playerCount = teams[i][6];
        teamInfo.remainingPlayerCount = teams[i][7];
        teamInfo.maxAllowedBidOnNextPlayer = teams[i][8];
        teamInfo.ownerPlayerId = teams[i][9];
        teamInfo.retainedPlayerId = teams[i][10];
        returnData[teamInfo.name] = teamInfo;
      }
      return returnData;
    }

    const getAuctionData = (xcelData) => {
      let auctionData = {};
      for (let i = 0; i < xcelData.length; i++) {
        switch (xcelData[i].name) {
          case 'MTPL Players':
            auctionData.mtplPlayers = xcelData[i];
            break;
          case 'MTPL Teams':
            auctionData.mtplTeams = xcelData[i];
            break;
          default:
            break;
        }
      }
      return auctionData;
    }

    const createPlayerProfile = () => {
      let xcelData = this.props.getSheetsData();
      console.log("No of Sheets : ", xcelData.length);
      let auctionData = getAuctionData(xcelData);
      console.log('MTPL Players : ', auctionData.mtplPlayers);
      console.log('MTPL Teams : ', auctionData.mtplTeams);

      let players = populatePlayerInfo(auctionData.mtplPlayers);
      let teams = populateTeams(auctionData.mtplTeams);

