import ReactGoogleSheets from 'react-google-sheets';
import * as Constants from './Constants'
import React, { Component } from 'react';
import './App.css';
import Player from './Player.js'
import Select from 'react-select';

class DataComponent extends Component {

  constructor(props) {
    super(props)
    this.state = {
      sheetLoaded: false,
      auctionWebData: {},
      currentPlayerId: '',
      nextPlayerId: '',
      prevPlayerId: ''
    }
  }


  handleChange = selectedOption => {
    console.log(`Option selected:`, selectedOption);
  };

  populateTeams = (mtplTeams) => {
    let returnData = [];
    let teams = mtplTeams.data;
    for (let i = 3; i < teams.length; i++) {
      let teamInfo = {};
      teamInfo.name = teams[i][0];
      teamInfo.ownerName = teams[i][1];
      teamInfo.isOwnerPlayer = teams[i][2];
      teamInfo.amount = teams[i][3];
      teamInfo.amountSpent = teams[i][4];
      teamInfo.amountBalance = teams[i][5];
      teamInfo.playerCount = teams[i][6];
      teamInfo.remainingPlayerCount = teams[i][7];
      teamInfo.maxAllowedBidOnNextPlayer = teams[i][8];
      teamInfo.ownerPlayerId = teams[i][9];
      teamInfo.retainedPlayerId = teams[i][10];
      returnData.push(teamInfo);
    }
    return returnData;
  };

  getAuctionData = (xcelData) => {
    let auctionData = {};
    for (let i = 0; i < xcelData.length; i++) {
      switch (xcelData[i].name) {
        case 'MTPL Players':
          auctionData.mtplPlayers = xcelData[i];
          break;
        case 'MTPL Teams':
          auctionData.mtplTeams = xcelData[i];
          break;
        default:
          break;
      }
    }
    return auctionData;
  };

  retrieveAuctionData = () => {
    let xcelData = this.props.getSheetsData();
    console.log("No of Sheets : ", xcelData.length);
    let auctionWebData = this.getAuctionData(xcelData);
    console.log('MTPL Players : ', auctionWebData.mtplPlayers);
    console.log('MTPL Teams : ', auctionWebData.mtplTeams);

    let returnData = {};

    returnData.players = this.populatePlayerInfo(auctionWebData.mtplPlayers, this.populateTeams(auctionWebData.mtplTeams));
    console.log("MTPL Players : ", returnData.players);
    return returnData;
  };

  render() {

    const { selectedOption } = this.state;

    const teamOptions = (mtplTeams) => {
      let returnData = [];
      let teams = mtplTeams.data;
      for (let i = 3; i < teams.length; i++) {
        let teamInfo = {};
        teamInfo.value = teams[i][0];
        teamInfo.label = teams[i][0];
        returnData.push(teamInfo);
      }
      return returnData;
    };

      return (
        <div>
         <ReactGoogleSheets
          clientId={Constants.CLIENT_ID}
          apiKey={Constants.API_KEY}
          spreadsheetId={Constants.MTPL_MENS_SPREADSHEET_ID}
          afterLoading={() => this.setState({ sheetLoaded: true })}
        >
          {this.state.sheetLoaded ? this.retrieveAuctionData().players.map((item, key) =>

          /*  Player item={item} id={key} key={key} /> */

          <div key={key} className="AuctionBody">
            <div>
              <button onClick={() => { }}>Previous</button>

              <button onClick={() => { }}>Next</button>
            </div>

            {item.isExternalPlayer === 'TRUE' ? <div className="externalPlayer"> Associated with Other MN Cricket League(s) </div> : ''}

            <div className="basePrice">
              <span>Base Price : {item.basePrice}</span>
            </div>
            <div className="playerName">
              <span>{item.name}</span>
            </div>
            <div className="playerInfo">
              <span>Rank: {item.overallRank}</span>
              <span>MTBC Team: {item.mtbcTeamName}</span>

            </div>
            <div className="holdMTPLSpace" />
            <div className="mtplPlayerInfo">

              {item.mtplTeamName ?
                <span>MTPL Team: {item.mtplTeamName} </span> :
                <form onSubmit={this.handleSubmit}>
                  <div>
                  <label>
                    Price:
                  </label>
                  <input type="text" value={this.state.value} onChange={this.handleChange} />
                  </div>
                  <div>
                  <label>
                    MTPL Team:
                  </label>
                  <Select
                    value={selectedOption}
                    onChange={this.handleChange}
                    options={teamOptions && teamOptions === Array ? teamOptions : []}
                  />
                  </div>
                  <input type="submit" value="Submit" />
                </form>
              }
            </div>
            <div className="holdEmptySpace" />
            <div className="table">
              <table>
                <tr>
                    <td className="td">
                      Matches
                    </td>
                    <td className="td">
                      &nbsp;
                    </td>
                    <td className="td">
                      Rank
                    </td>
                    <td className="td">
                      Runs
                    </td>
                    <td className="td">
                      S/R
                    </td>
                    <td className="td">
                      HS
                    </td>
                    <td className="td">
                      Ave
                    </td>
                    <td className="td">
                      (C)
                    </td>
                    <td className="td">
                      &nbsp;
                    </td>
                    <td className="td">
                      Rank
                    </td>
                    <td className="td">
                      (W)
                    </td>
                    <td className="td">
                      (O)
                    </td>
                    <td className="td">
                      Econ
                    </td>
                    <td className="td">
                      BB
                    </td>
                    <td className="td">
                      H/T
                    </td>
                </tr>
                <tr>
                  <td className="td">
                    {item.matches}
                  </td>
                  <td className="td">
                    &nbsp;
                    </td>
                  <td className="td">
                    {item.batRank}
                  </td>
                  <td className="td">
                    {item.runs}
                  </td>
                  <td className="td">
                    {item.batSR}
                  </td>
                  <td className="td">
                    {item.highestScore}
                  </td>
                  <td className="td">
                    {item.batAverage}
                  </td>
                  <td className="td">
                    {item.catches}
                  </td>
                  <td className="td">
                    &nbsp;
                    </td>
                  <td className="td">
                    {item.bowlRank}
                  </td>
                  <td className="td">
                    {item.wickets}
                  </td>
                  <td className="td">
                    {item.bowlOvers}
                  </td>
                  <td className="td">
                    {item.economy}
                  </td>
                  <td className="td">
                    {item.bestBowl}
                  </td>
                  <td className="td">
                    {item.hatTrick}
                  </td>
                </tr>

              </table>
            </div>


            {/* Update Data */}
            <button onClick={() => {
              this.props.updateCell(
                'MTPL Players', // sheetName
                'G', // column
                4, // row
                'Sholay - 11', // value
                null, // successCallback
                (error) => {
                  console.log('error', error)
                } // errorCallback
              );
              this.props.updateCell(
                'MTPL Players', // sheetName
                'I', // column
                4, // row
                '50000', // value
                null, // successCallback
                (error) => {
                  console.log('error', error)
                } // errorCallback
              );
            }}>update cell!</button>
          </div>

          ) : 'loading...'}
        </ReactGoogleSheets>

      </div>
        )
      }
    }

    export default ReactGoogleSheets.connect(DataComponent);
